import React, { useState, useEffect, useCallback, useRef } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import axios from 'axios';
import '../styles/ItemList.css';

const API_URL = 'http://localhost:5000/api';

const ItemList = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [search, setSearch] = useState('');
  const [total, setTotal] = useState(0);
  const [sortConfig, setSortConfig] = useState({ key: null, direction: 'asc' });
  const observer = useRef();

  // Function to fetch items with pagination
  const fetchItems = useCallback(async (pageNum = 0, searchTerm = '') => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/items`, {
        params: {
          page: pageNum,
          limit: 20,
          search: searchTerm
        }
      });
      
      const { items: newItems, total } = response.data;
      
      setTotal(total);
      
      if (pageNum === 0) {
        setItems(newItems);
      } else {
        setItems(prev => [...prev, ...newItems]);
      }
      
      setHasMore(newItems.length === 20);
      setPage(pageNum);
    } catch (error) {
      console.error('Error fetching items:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  // Initial fetch
  useEffect(() => {
    fetchItems(0, search);
  }, [fetchItems, search]);

  // Intersection observer for infinite scroll
  const lastItemRef = useCallback(node => {
    if (loading) return;
    if (observer.current) observer.current.disconnect();
    
    observer.current = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && hasMore) {
        fetchItems(page + 1, search);
      }
    });
    
    if (node) observer.current.observe(node);
  }, [loading, hasMore, page, fetchItems, search]);

  // Handle item selection
  const handleItemSelect = async (id, selected) => {
    try {
      await axios.post(`${API_URL}/items/selection`, { id, selected });
      
      setItems(prev => 
        prev.map(item => 
          item.id === id ? { ...item, selected } : item
        )
      );
    } catch (error) {
      console.error('Error updating selection:', error);
    }
  };

  // Handle search input
  const handleSearchChange = (e) => {
    setSearch(e.target.value);
    setPage(0);
  };

  // Handle column sorting
  const handleSort = (key) => {
    let direction = 'asc';
    if (sortConfig.key === key && sortConfig.direction === 'asc') {
      direction = 'desc';
    }
    setSortConfig({ key, direction });

    const sortedItems = [...items].sort((a, b) => {
      if (a[key] < b[key]) {
        return direction === 'asc' ? -1 : 1;
      }
      if (a[key] > b[key]) {
        return direction === 'asc' ? 1 : -1;
      }
      return 0;
    });

    setItems(sortedItems);
  };

  // Handle drag and drop reordering
  const handleDragEnd = async (result) => {
    if (!result.destination) return;
    
    const startIndex = result.source.index;
    const endIndex = result.destination.index;
    
    if (startIndex === endIndex) return;
    
    const reorderedItems = Array.from(items);
    const [removed] = reorderedItems.splice(startIndex, 1);
    reorderedItems.splice(endIndex, 0, removed);
    
    setItems(reorderedItems);
    
    // Send new order to server
    try {
      await axios.post(`${API_URL}/items/reorder`, {
        newOrder: reorderedItems.map(item => item.id)
      });
    } catch (error) {
      console.error('Error updating item order:', error);
    }
  };

  // Get sort indicator
  const getSortIndicator = (key) => {
    if (sortConfig.key !== key) return '↕';
    return sortConfig.direction === 'asc' ? '↑' : '↓';
  };

  const renderItems = () => {
    return items.map((item, index) => (
      <Draggable
        key={item.id}
        draggableId={item.id.toString()}
        index={index}
      >
        {(provided) => (
          <div
            className="table-row"
            ref={index === items.length - 1 ? lastItemRef : null}
            {...provided.draggableProps}
            {...provided.dragHandleProps}
          >
            <div className="cell select">
              <input
                type="checkbox"
                checked={item.selected || false}
                onChange={(e) => handleItemSelect(item.id, e.target.checked)}
              />
            </div>
            <div className="cell id">{item.id}</div>
            <div className="cell value">{item.value}</div>
          </div>
        )}
      </Draggable>
    ));
  };

  return (
    <div className="item-list-container">
      <div className="controls">
        <input
          type="text"
          placeholder="Search items..."
          value={search}
          onChange={handleSearchChange}
          className="search-input"
        />
        <div className="info">
          Showing {items.length} of {total} items
        </div>
      </div>
      
      <div className="items-table">
        <div className="table-header">
          <div className="cell select">Select</div>
          <div 
            className="cell id sortable"
            onClick={() => handleSort('id')}
          >
            ID {getSortIndicator('id')}
          </div>
          <div 
            className="cell value sortable"
            onClick={() => handleSort('value')}
          >
            Value {getSortIndicator('value')}
          </div>
        </div>

        <DragDropContext onDragEnd={handleDragEnd}>
          <Droppable droppableId="droppable">
            {(provided) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                className="items-list"
              >
                {renderItems()}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>
        
        {loading && <div className="loading">Loading more items...</div>}
      </div>
    </div>
  );
};

export default ItemList;
